sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
		if ( newValue == '') {
			return;
		}

		// Récupère la valeur du champ 'What Needed' avant le chargement du formulaire
		var whatneeded = g_form.getValue('u_what_needed');

		// Effacez tous les choix de la liste de choix 'What Needed'
		g_form.clearOptions('u_what_needed');

		// Si la valeur du champ Type de requête est 'hr', ajoutez
		// deux choix 'hr' et 'other' dans la liste de choix du champ 'What Needed'
		if(newValue == 'hr'){
			g_form.addOption('u_what_needed','hr1','Human Resources 1');
			g_form.addOption('u_what_needed','hr2','Human Resources 2');
			g_form.addOption('u_what_needed','other','Other');
		}

		// Si la valeur du champ Type de requête est 'facilities', ajoutez
		// deux choix 'facilities' et 'other' dans la liste de choix du champ 'What Needed'
		if(newValue == 'facilities'){
			g_form.addOption('u_what_needed','facilities1','Facilities 1');
			g_form.addOption('u_what_needed','facilities2','Facilities 2');
			g_form.addOption('u_what_needed','other','Other');
		}

		// Si la valeur du champ Type de requête est 'legal', ajoutez
		// deux choix 'legal' et 'other' dans la liste de choix du champ 'What Needed'
		if(newValue == 'legal'){
			g_form.addOption('u_what_needed','legal1','Legal 1');
			g_form.addOption('u_what_needed','legal2','Legal 2');
			g_form.addOption('u_what_needed','other','Other');
		}

		// Si le formulaire est en cours de chargement et qu'il ne s'agit pas d'un nouvel enregistrement,
		// définissez la valeur 'u_what_needed' sur la valeur de l'enregistrement avant son chargement
		if(isLoading && !g_form.isNewRecord()){
			g_form.setValue('u_what_needed', whatneeded);
		}
	}
